import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import HAYQAbi from '../abis/HAYQ.json';
import { useTranslation } from 'react-i18next';

function EconomicGrowth({ contractAddress }) {
  const [apr, setApr] = useState(0);
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");
  const { t } = useTranslation();

  const fetchAPR = async () => {
    if (!window.ethereum || !contractAddress) {
      setStatus(t("metamask_or_contract_missing"));
      return;
    }

    setLoading(true);
    setStatus(t("fetchingAPR"));

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, HAYQAbi, provider);

      const value = await contract.apr(); // Solidity ֆունկցիա, uint256
      // فرضենք 2 decimals՝ օրինակ 500 = 5.00%
      setApr(Number(ethers.formatUnits(value, 2)));
      setStatus(t("aprFetched"));
    } catch (err) {
      console.error("Failed to fetch APR:", err);
      setApr(0);
      setStatus(t("fetchFailed"));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAPR();
    const interval = setInterval(fetchAPR, 30000); // ավտոմատ թարմացում
    return () => clearInterval(interval);
  }, [contractAddress]);

  return (
    <div className="component">
      <h3>{t("economicGrowth")}</h3>
      <p>{apr} %</p>
      <button className="primary" onClick={fetchAPR} disabled={loading}>
        {loading ? t("processing") : t("refresh")}
      </button>
      {status && <p>{status}</p>}
    </div>
  );
}

export default EconomicGrowth;
