// src/App.jsx
import React, { useState } from "react";
import { useTranslation } from "react-i18next";

import WalletConnect from "./components/WalletConnect";
import Balance from "./components/Balance";
import Transfer from "./components/Transfer";
import Stake from "./components/Stake";
import Unstake from "./components/Unstake";
import Buyback from "./components/Buyback";
import Voting from "./components/Voting";
import LiveChart from "./components/LiveChart";
import DividendClaim from "./components/DividendClaim";
import EconomicGrowth from "./components/EconomicGrowth";

import "./style.css";

function App() {
  const { t, i18n } = useTranslation();
  const [contractAddress, setContractAddress] = useState("0xBF3cfF21BD17854334112d28853fe716Eb423536");
  const [account, setAccount] = useState(""); // WalletConnect–ից account
  const [aprHistory, setAprHistory] = useState([]); // LiveChart data

  const changeLanguage = (lng) => i18n.changeLanguage(lng);

  // LiveChart updater callback, փոխանցվում է EconomicGrowth.jsx-ին
  const updateAPRHistory = (aprValue) => {
    setAprHistory(prev => [
      ...prev,
      { time: new Date().toLocaleTimeString(), value: aprValue }
    ]);
  };

  return (
    <div className="app-container">
      <h1>{t("welcome")}</h1>

      {/* Լեզվի կոճակներ */}
      <div className="lang-switcher">
        {["en","hy","ru","fr","es","de","zh","ja","ar"].map((lng) => (
          <button key={lng} onClick={() => changeLanguage(lng)}>
            {lng.toUpperCase()}
          </button>
        ))}
      </div>

      {/* Wallet Connect */}
      <WalletConnect setAccount={setAccount} />

      {/* Contract Address input */}
      <div className="component">
        <input
          placeholder={t("contractAddress")}
          value={contractAddress}
          onChange={(e) => setContractAddress(e.target.value)}
        />
      </div>

      {/* HAYQ Modules */}
      <Balance contractAddress={contractAddress} account={account} />
      <Transfer contractAddress={contractAddress} account={account} />
      <Stake contractAddress={contractAddress} account={account} />
      <Unstake contractAddress={contractAddress} account={account} />
      <Buyback contractAddress={contractAddress} account={account} />
      <Voting contractAddress={contractAddress} account={account} />
      <DividendClaim contractAddress={contractAddress} account={account} />

      {/* EconomicGrowth with APR history update */}
      <EconomicGrowth
        contractAddress={contractAddress}
        account={account}
        onAprUpdate={updateAPRHistory}
      />

      {/* LiveChart shows APR history */}
      <LiveChart data={aprHistory} />
    </div>
  );
}

export default App;
