import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import HAYQAbi from '../abis/HAYQ.json';
import { useTranslation } from 'react-i18next';

function DividendClaim({ contractAddress }) {
  const { t } = useTranslation();
  const [withdrawable, setWithdrawable] = useState("0");
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");

  const fetchWithdrawable = async () => {
    if (!window.ethereum || !contractAddress) return;
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      const accounts = await provider.send("eth_requestAccounts", []);
      const amount = await contract.withdrawableDividendOf(accounts[0]);
      setWithdrawable(ethers.formatUnits(amount, 18));
    } catch(err) {
      console.error("Failed to fetch withdrawable dividend:", err);
      setStatus(t("fetchFailed"));
    }
  };

  const claim = async () => {
    if (!window.ethereum || !contractAddress) return;

    setLoading(true);
    setStatus(t("claimPending"));

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      const tx = await contract.withdrawDividend({ gasLimit: 500000 });
      console.log("Claim TX:", tx.hash);
      await tx.wait();

      setStatus(t("dividendsClaimed"));
      await fetchWithdrawable();
    } catch(err) {
      console.error(err);
      if (err.code === 4001) {
        setStatus(t("txRejected"));
      } else {
        setStatus(t("claimFailed"));
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWithdrawable();
    const interval = setInterval(fetchWithdrawable, 30000);
    return () => clearInterval(interval);
  }, [contractAddress]);

  return (
    <div className="component">
      <h3>{t('claimDividends')}</h3>
      <p>{t('withdrawable')}: {withdrawable} HAYQ</p>
      <button className="primary" onClick={claim} disabled={loading}>
        {loading ? t("processing") : t("claim")}
      </button>
      <button className="secondary" onClick={fetchWithdrawable} disabled={loading}>
        {t('refresh')}
      </button>
      {status && <p>{status}</p>}
    </div>
  );
}

export default DividendClaim;
