// src/components/WalletConnect.jsx
import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

function WalletConnect() {
  const { t } = useTranslation();
  const [account, setAccount] = useState(null);
  const [loading, setLoading] = useState(false);

  const connectWallet = async () => {
    if (!window.ethereum) {
      alert(t("noMetamask"));
      return;
    }
    setLoading(true);
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
    } catch (err) {
      console.error(err);
      alert(t("walletError"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="component">
      <button onClick={connectWallet} disabled={loading}>
        {loading ? t("connecting") : account ? `${t("connected")}: ${account}` : t("connectWallet")}
      </button>
    </div>
  );
}

export default WalletConnect;

// Example i18n resources
export const resources = {
  en: {
    translation: {
      "connectWallet": "Connect Wallet",
      "connected": "Connected",
      "connecting": "Connecting…",
      "noMetamask": "MetaMask not detected",
      "walletError": "Wallet connection failed"
    }
  },
  hy: {
    translation: {
      "connectWallet": "Միացնել դրամապանակը",
      "connected": "Միացված է",
      "connecting": "Միացում…",
      "noMetamask": "MetaMask չի հայտնաբերվել",
      "walletError": "Դրամապանակի միացումը ձախողվեց"
    }
  },
  ru: {
    translation: {
      "connectWallet": "Подключить кошелек",
      "connected": "Подключен",
      "connecting": "Подключение…",
      "noMetamask": "MetaMask не обнаружен",
      "walletError": "Ошибка подключения кошелька"
    }
  }
};
