// src/components/DividendClaim.jsx
import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import HAYQAbi from '../abis/HAYQMiniMVP.json';
import { useTranslation } from 'react-i18next';

function DividendClaim({ contractAddress, account, provider, signer }) {
  const { t } = useTranslation();
  const [withdrawable, setWithdrawable] = useState("0");
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");

  const fetchWithdrawable = async () => {
    if (!window.ethereum || !contractAddress || !account) return;
    try {
      const contract = new ethers.Contract(contractAddress, HAYQAbi, provider || signer);

      if (typeof contract.withdrawable !== 'function') {
        console.warn("Contract function missing: withdrawable");
        setWithdrawable("0");
        return;
      }

      const raw = await contract.withdrawable(account);
      const value = typeof raw === 'bigint' ? raw : BigInt(raw.toString());
      setWithdrawable(Number(value) / 1e18);
      console.log("Withdrawable:", value);
    } catch (err) {
      console.error("Failed to fetch withdrawable:", err);
      setWithdrawable("0");
    }
  };

  const claim = async () => {
    if (!window.ethereum || !contractAddress || !account) return;
    if (Number(withdrawable) <= 0) {
      setStatus(t("noDividends"));
      return;
    }

    setLoading(true);
    setStatus(t("claim_pending"));
    try {
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);
      if (typeof contract.claimDividends !== 'function') {
        console.warn("Contract function missing: claimDividends");
        setStatus(t("claim_failed") + ": function not available");
        return;
      }

      const tx = await contract.claimDividends({ gasLimit: 300000 });
      setStatus(`${t("txSent")}: ${tx.hash}`);
      await tx.wait();
      setStatus(t("claim_success"));
      fetchWithdrawable(); // թարմացնենք withdrawal
    } catch (err) {
      console.error("Claim error:", err);
      const message = err?.error?.message || err?.message || String(err);
      setStatus(`${t("claim_failed")}: ${message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWithdrawable();
    const interval = setInterval(fetchWithdrawable, 30000);
    return () => clearInterval(interval);
  }, [contractAddress, account]);

  return (
    <div className="component">
      <h3>{t("claimDividends")}</h3>
      <p>{t("withdrawable")}: {withdrawable} HAYQ</p>
      <button className="primary" onClick={claim} disabled={loading || Number(withdrawable) <= 0}>
        {loading ? t("processing") : t("claimrefresh")}
      </button>
      {status && <p>{status}</p>}
    </div>
  );
}

export default DividendClaim;
