import React, { useState, useContext } from 'react';
import { ethers } from 'ethers';
import { useTranslation } from 'react-i18next';
import HAYQAbi from '../abis/HAYQ.json';
import { AppContext } from '../context/AppContext';

function Buyback({ contractAddress }) {
  const { t } = useTranslation();
  const { setStatusMessage } = useContext(AppContext);
  const [loading, setLoading] = useState(false);

  const triggerBuyback = async () => {
    if (!window.ethereum || !contractAddress) return;

    setLoading(true);
    setStatusMessage(t("buyback_pending"));
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      const tx = await contract.buyback(ethers.parseUnits("100", 18), ethers.parseUnits("0.01", 18));
      await tx.wait();

      setStatusMessage(t("buyback_success"));
    } catch(err) {
      console.error(err);
      setStatusMessage(t("buyback_failed"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="component">
      <h2>{t("buyback")}</h2>
      <button className="primary" onClick={triggerBuyback} disabled={loading}>
        {loading ? t("processing") : t("buyback_trigger")}
      </button>
    </div>
  );
}

export default Buyback;
