// src/components/Buyback.jsx
import React, { useState } from "react";
import { ethers } from "ethers";
import { useTranslation } from "react-i18next";
import HAYQAbi from "../abis/HAYQ.json";

function Buyback({ contractAddress, provider }) {
  const { t } = useTranslation();
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);

  const triggerBuyback = async () => {
    if (!provider || !contractAddress) return;

    try {
      setLoading(true);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      // ⚠️ Այստեղ փոխելու ենք կոնտրակտի իրական buyback ֆունկցիայի անունը
      const tx = await contract.buyback(
        ethers.parseUnits(amount || "0", 18),
        { gasLimit: 500000 }
      );

      await tx.wait();
      alert(t("buyback_success"));
    } catch (err) {
      console.error(err);
      alert(t("buyback_failed"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="component">
      <h3>{t("buyback")}</h3>

      <input
        placeholder={t("amount")}
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />

      <button className="primary" disabled={loading} onClick={triggerBuyback}>
        {loading ? t("loading") : t("buyback")}
      </button>
    </div>
  );
}

export default Buyback;
