import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import { useTranslation } from "react-i18next";
import HAYQAbi from "../abis/HAYQMiniMVP.json";

export default function Buyback({ contractAddress, provider, signer, account }) {
  const { t } = useTranslation();

  const [isOwner, setIsOwner] = useState(false);
  const [contractBalance, setContractBalance] = useState("0");
  const [amount, setAmount] = useState("");
  const [minOut, setMinOut] = useState("");
  const [status, setStatus] = useState("");

  useEffect(() => {
    if (!provider || !contractAddress) return;

    (async () => {
      try {
        const contract = new ethers.Contract(contractAddress, HAYQAbi, provider);

        // Check owner
        const owner = await contract.owner();
        setIsOwner(owner.toLowerCase() === account?.toLowerCase());

        // Contract balance
        const bal = await contract.balanceOf(contractAddress);
        setContractBalance(ethers.formatUnits(bal, 18));
      } catch (err) {
        console.error("Buyback init error:", err);
      }
    })();
  }, [provider, contractAddress, account]);

  const performBuyback = async () => {
    if (!isOwner) {
      setStatus(t("ownerOnlyNotice"));
      return;
    }

    if (!amount || !minOut) {
      setStatus(t("fillFields"));
      return;
    }

    try {
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      const tx = await contract.buyback(
        ethers.parseUnits(amount, 18),
        ethers.parseUnits(minOut, 18),
        { gasLimit: 200000 }
      );

      setStatus(`${t("txSent")}: ${tx.hash}`);
      await tx.wait();

      setStatus(t("buyback_success"));
    } catch (err) {
      console.error("Buyback error:", err);
      setStatus(t("buyback_failed") + ": " + (err?.message || String(err)));
    }
  };

  return (
    <div className="component">
      <h3>{t("buyback")}</h3>

      <p>
        <b>{t("contractBalance")}:</b> {contractBalance} HAYQ
      </p>

      {!isOwner ? (
        <p style={{ color: "orange" }}>
          {t("ownerOnlyNotice")}
        </p>
      ) : (
        <>
          <input
            type="number"
            placeholder={t("amount")}
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />

          <input
            type="number"
            placeholder={t("minOut")}
            value={minOut}
            onChange={(e) => setMinOut(e.target.value)}
          />

          <button className="primary" onClick={performBuyback}>
            {t("executeBuyback")}
          </button>
        </>
      )}

      {status && <p>{status}</p>}
    </div>
  );
}
