// src/components/EconomicGrowth.jsx
import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import HAYQAbi from '../abis/HAYQMiniMVP.json';
import { useTranslation } from 'react-i18next';

function EconomicGrowth({ contractAddress, account, onAprUpdate }) {
  const { t } = useTranslation();
  const [apr, setApr] = useState(0);
  const [loading, setLoading] = useState(false);

  const fetchAPR = async () => {
    if (!window.ethereum || !contractAddress || !account) return;

    setLoading(true);
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, HAYQAbi, provider);

      const stakedBN = await contract.stakedBalanceOf(account);
      const totalSupplyBN = await contract.totalSupply();

      // Վերածում թիվի % (BigInt գործառույթներ)
      const aprValue = totalSupplyBN > 0n
        ? Number((stakedBN * 10000n) / totalSupplyBN) / 100
        : 0;

      setApr(aprValue);
      if (onAprUpdate) onAprUpdate(aprValue);
    } catch (err) {
      console.error("EconomicGrowth fetchAPR error:", err);
      setApr(0);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAPR();
    const interval = setInterval(fetchAPR, 30000); // 30 վայրկյանը մեկ թարմացում
    return () => clearInterval(interval);
  }, [contractAddress, account]);

  return (
    <div className="component">
      <h3>{t("economicGrowth")}</h3>
      <p>{apr} %</p>
      <button className="primary" onClick={fetchAPR} disabled={loading}>
        {loading ? t("processing") : t("refresh")}
      </button>
    </div>
  );
}

export default EconomicGrowth;
