// src/components/Voting.jsx
import React, { useState } from 'react';
import { ethers } from 'ethers';
import { useTranslation } from 'react-i18next';
import HAYQAbi from '../abis/HAYQ.json';

function Voting({ contractAddress }) {
  const { t } = useTranslation();
  const [proposal, setProposal] = useState('');
  const [loading, setLoading] = useState(false);

  const submitVote = async () => {
    if (!window.ethereum || !contractAddress) return;

    if (!proposal.trim()) {
      alert(t("enterProposal"));
      return;
    }

    setLoading(true);
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      // MVP-ի համար snapshot simulate ենք անում
      const tx = await contract.snapshot();
      await tx.wait();

      alert(t('voteSuccess'));
      setProposal('');
    } catch (err) {
      console.error(err);
      alert(t('voteFailed'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="component">
      <h3>{t("votingSnapshot")}</h3>
      <input
        placeholder={t("proposal")}
        value={proposal}
        onChange={e => setProposal(e.target.value)}
        disabled={loading}
      />
      <button className="primary" onClick={submitVote} disabled={loading}>
        {loading ? t("processing") : t("submit")}
      </button>
    </div>
  );
}

export default Voting;
