import React, { useState } from 'react';
import { ethers } from 'ethers';
import { useTranslation } from 'react-i18next';
import HAYQAbi from '../abis/HAYQ.json';

function Buyback({ contractAddress }) {
  const { t } = useTranslation();
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");

  const triggerBuyback = async () => {
    if (!window.ethereum) {
      setStatus(t("metamask_missing"));
      return;
    }

    if (!contractAddress) {
      setStatus(t("contract_missing"));
      return;
    }

    setLoading(true);
    setStatus(t("buyback_pending"));

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      // Օրինակ՝ 100 HAYQ, 0.01 ETH minAmount
      const tx = await contract.buyback(
        ethers.parseUnits("100", 18),
        ethers.parseUnits("0.01", 18),
        { gasLimit: 500000 } // կանխարգելում TX fail
      );

      console.log("Buyback TX Hash:", tx.hash);
      await tx.wait();

      setStatus(t("buyback_success"));
    } catch (err) {
      console.error(err);

      // Մեկ փոքր user-friendly message
      if (err.code === 4001) {
        // User rejected tx
        setStatus(t("buyback_rejected"));
      } else {
        setStatus(t("buyback_failed"));
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="component">
      <h2>{t("buyback")}</h2>
      <button className="primary" onClick={triggerBuyback} disabled={loading}>
        {loading ? t("processing") : t("buyback_trigger")}
      </button>
      {status && <p>{status}</p>}
    </div>
  );
}

export default Buyback;
