import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { useTranslation } from "react-i18next";

import WalletConnect from "./components/WalletConnect";
import Balance from "./components/Balance";
import Transfer from "./components/Transfer";
import Stake from "./components/Stake";
import Unstake from "./components/Unstake";
import Buyback from "./components/Buyback";
import Voting from "./components/Voting";
import LiveChart from "./components/LiveChart";
import DividendClaim from "./components/DividendClaim";
import EconomicGrowth from "./components/EconomicGrowth";
import Footer from "./components/Footer";

import "./style.css";

function App() {
  const { t, i18n } = useTranslation();

  // Contract & Wallet
  const [contractAddress, setContractAddress] = useState(
    "0x7E5c8baC4447D8FA7010AEc8D400Face1b1BEC83"
  );
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState("");

  // LiveChart data
  const [aprHistory, setAprHistory] = useState([]);

  const [darkMode, setDarkMode] = useState(true); // Default: Dark Theme (Code Art style)

  const changeLanguage = (lng) => i18n.changeLanguage(lng);

  // Connect Wallet
  const connectWallet = async () => {
    if (!window.ethereum) {
      alert("Install MetaMask!");
      return;
    }
    try {
      await window.ethereum.request({ method: "eth_requestAccounts" });
      const _provider = new ethers.BrowserProvider(window.ethereum);
      const _signer = await _provider.getSigner();
      const _account = await _signer.getAddress();

      setProvider(_provider);
      setSigner(_signer);
      setAccount(_account);

      window.ethereum.on("accountsChanged", async (accounts) => {
        setAccount(accounts[0] || "");
      });
      window.ethereum.on("chainChanged", async () => {
        const network = await _provider.getNetwork();
        console.log("Network changed:", network);
      });
    } catch (err) {
      console.error(err);
    }
  };

  const updateAPRHistory = (aprValue) => {
    setAprHistory((prev) => [
      ...prev,
      { time: new Date().toLocaleTimeString(), value: aprValue },
    ]);
  };

  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  // Mouse tracking for glow effect
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  return (
    <div className={`app-container ${darkMode ? "dark" : "light"}`}>
      {/* Background Glow */}
      <div
        className="glow-effect"
        style={{
          background: `radial-gradient(circle at ${mousePos.x}px ${mousePos.y}px, rgba(242,168,0,0.1) 0%, transparent 40%)`,
        }}
      ></div>

      {/* Header with Logo as a "code block" */}
      <header className="app-header">
        <div className="code-block">
          <span className="line-number">1</span>
          <span className="keyword">const</span> <span className="variable">HAYQ</span> = <span className="string">"Հայկական Ստեյբլկոին"</span>;
        </div>
        <h1>{t("welcome")}</h1>
      </header>

      {/* Language Switcher as "terminal command" */}
      <div className="lang-switcher">
        {["en", "hy", "ru", "fr", "es", "de", "zh", "ja", "ar"].map((lng) => (
          <button
            key={lng}
            onClick={() => changeLanguage(lng)}
            className="terminal-button"
          >
            <span className="prompt">$</span> lang {lng.toUpperCase()}
          </button>
        ))}
      </div>

      <WalletConnect setAccount={setAccount} />

      <div className="component code-component">
        <div className="code-line">
          <span className="line-number">2</span>
          <span className="keyword">const</span> <span className="variable">contract</span> = <span className="string">"{contractAddress}"</span>;
        </div>
        <input
          placeholder={t("contractAddress")}
          value={contractAddress}
          onChange={(e) => setContractAddress(e.target.value)}
          className="code-input"
        />
      </div>

      <Balance contractAddress={contractAddress} provider={provider} signer={signer} />
      <Transfer contractAddress={contractAddress} provider={provider} signer={signer} />
      <Stake contractAddress={contractAddress} provider={provider} signer={signer} />
      <Unstake contractAddress={contractAddress} provider={provider} signer={signer} />
      <Buyback contractAddress={contractAddress} provider={provider} signer={signer} account={account} />
      <Voting contractAddress={contractAddress} provider={provider} signer={signer} account={account} />
      <DividendClaim contractAddress={contractAddress} provider={provider} signer={signer} />
      <EconomicGrowth contractAddress={contractAddress} provider={provider} signer={signer} onAprUpdate={updateAPRHistory} />
      <LiveChart data={aprHistory} />

      <Footer />
    </div>
  );
}

export default App;
