import React, { useState } from 'react';
import { ethers } from 'ethers';
import { useTranslation } from 'react-i18next';
import HAYQAbi from '../abis/HAYQ.json';

function Voting({ contractAddress }) {
  const { t } = useTranslation();
  const [proposal, setProposal] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");

  const submitVote = async () => {
    if (!window.ethereum) {
      setStatus(t("metamask_missing"));
      return;
    }

    if (!contractAddress) {
      setStatus(t("contract_missing"));
      return;
    }

    if (!proposal.trim()) {
      alert(t("enterProposal"));
      return;
    }

    setLoading(true);
    setStatus(t("vote_pending"));

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      // MVP-ի համար snapshot simulate ենք անում
      // ⚠️ Եթե իրական voting function կա, փոխել contract.snapshot() -> contract.voteFor(proposalId)
      const tx = await contract.snapshot({
        gasLimit: 500000
      });
      await tx.wait();

      setStatus(t('voteSuccess'));
      setProposal('');
    } catch (err) {
      console.error(err);
      if (err.code === 4001) {
        setStatus(t('voteRejected'));
      } else {
        setStatus(t('voteFailed'));
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="component">
      <h3>{t("votingSnapshot")}</h3>
      <input
        placeholder={t("proposal")}
        value={proposal}
        onChange={e => setProposal(e.target.value)}
        disabled={loading}
      />
      <button className="primary" onClick={submitVote} disabled={loading}>
        {loading ? t("processing") : t("submit")}
      </button>
      {status && <p>{status}</p>}
    </div>
  );
}

export default Voting;
