import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { useHAYQData } from "./hooks/useHAYQData";

import WalletConnect from "./components/WalletConnect";
import Balance from "./components/Balance";
import Transfer from "./components/Transfer";
import Stake from "./components/Stake";
import Unstake from "./components/Unstake";
import Buyback from "./components/Buyback";
import Voting from "./components/Voting";
import LiveChart from "./components/LiveChart";
import DividendClaim from "./components/DividendClaim";
import EconomicGrowth from "./components/EconomicGrowth";
import Footer from "./components/Footer";

import "./style.css";

function App() {
  const { t, i18n } = useTranslation();

  const [contractAddress, setContractAddress] = useState(
    "0x7E5c8baC4447D8FA7010AEc8D400Face1b1BEC83"
  );
  const [darkMode, setDarkMode] = useState(false);
  const [aprHistory, setAprHistory] = useState([]);

  // ---- TOKENOMICS ENGINE HOOK ----
  const {
    balance,
    staked,
    vesting,
    apr,
    stakingRatio,
    totalSupply,
    loading,
    fetchAll
  } = useHAYQData(contractAddress, (point) =>
    setAprHistory((prev) => [...prev, point])
  );

  const changeLanguage = (lng) => i18n.changeLanguage(lng);
  const toggleDarkMode = () => setDarkMode((p) => !p);

  return (
    <div className={`app-container ${darkMode ? "dark" : "light"}`}>
      {/* Header */}
      <header className="app-header">
        <img
          src="/public-assets/HAYQ_logo.png"
          alt="HAYQ Logo"
          className="logo"
          onClick={toggleDarkMode}
        />
        <h1>{t("welcome")}</h1>
      </header>

      {/* Language Switch */}
      <div className="lang-switcher">
        {["en","hy","ru","fr","es","de","zh","ja","ar"].map((lng) => (
          <button key={lng} onClick={() => changeLanguage(lng)}>
            {lng.toUpperCase()}
          </button>
        ))}
      </div>

      <WalletConnect />

      {/* Contract Address Input */}
      <div className="component">
        <input
          placeholder={t("contractAddress")}
          value={contractAddress}
          onChange={(e) => setContractAddress(e.target.value)}
        />
      </div>

      {/* TOKENOMICS Widgets */}
      <Balance value={balance} loading={loading} />
      <Transfer contractAddress={contractAddress} />
      <Stake contractAddress={contractAddress} />
      <Unstake contractAddress={contractAddress} />

      <Buyback contractAddress={contractAddress} />
      <Voting contractAddress={contractAddress} />

      <DividendClaim contractAddress={contractAddress} />

      <EconomicGrowth
        apr={apr}
        stakingRatio={stakingRatio}
        totalSupply={totalSupply}
        fetchAll={fetchAll}
      />

      <LiveChart data={aprHistory} />

      <Footer />
    </div>
  );
}

export default App;
