// src/components/DividendClaim.jsx
import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import HAYQAbi from '../abis/HAYQMiniMVP.json';
import { useTranslation } from 'react-i18next';

function DividendClaim({ contractAddress, account }) {
  const { t } = useTranslation();
  const [withdrawable, setWithdrawable] = useState("0");
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");

  const fetchWithdrawable = async () => {
    if (!window.ethereum || !contractAddress || !account) return;
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, HAYQAbi, provider);

      // Ստանում ենք vestingReleased գումարը որպես withdrawable
      let amount = "0";
      if (typeof contract.vestingReleased === "function") {
        amount = await contract.vestingReleased(account);
      } else {
        console.warn("vestingReleased function not found in contract");
      }

      setWithdrawable(ethers.formatUnits(amount, 18));
      console.log("Withdrawable:", amount.toString());
    } catch(err) {
      console.error("Failed to fetch withdrawable dividend:", err);
      setWithdrawable("0");
    }
  };

  const claim = async () => {
    if (!window.ethereum || !contractAddress || Number(withdrawable) <= 0) return;
    setLoading(true);
    setStatus(t("claim_pending"));

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, HAYQAbi, signer);

      if (typeof contract.withdrawDividend !== "function") {
        console.warn("withdrawDividend function not found in contract");
        setStatus(t("claim_failed") + ": function missing");
        return;
      }

      const tx = await contract.withdrawDividend({ gasLimit: 300000 });
      setStatus(`${t("txSent")}: ${tx.hash}`);
      await tx.wait();
      setStatus(t("claim_success"));

      await fetchWithdrawable(); // թարմացում
    } catch(err) {
      console.error("Claim error:", err);
      const message = err?.error?.message || err?.message || String(err);
      setStatus(`${t("claim_failed")}: ${message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWithdrawable();
    const interval = setInterval(fetchWithdrawable, 30000);
    return () => clearInterval(interval);
  }, [contractAddress, account]);

  return (
    <div className="component">
      <h3>{t("claimDividends")}</h3>
      <p>{t("withdrawable")}: {withdrawable} HAYQ</p>

      <button 
        className="primary" 
        onClick={claim} 
        disabled={loading || Number(withdrawable) <= 0}
      >
        {loading ? t("processing") : t("claim")}
      </button>

      <button 
        className="secondary" 
        onClick={fetchWithdrawable}
      >
        {t("refresh")}
      </button>

      {status && <p>{status}</p>}
    </div>
  );
}

export default DividendClaim;
