// src/components/EconomicGrowth.jsx
import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import HAYQAbi from '../abis/HAYQ.json';
import { useTranslation } from 'react-i18next';

function EconomicGrowth({ contractAddress }) {
  const [apr, setApr] = useState(0);
  const { t } = useTranslation();

  const fetchAPR = async () => {
    if (!window.ethereum || !contractAddress) return;
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, HAYQAbi, provider);
      const v = await contract.apr(); // Solidity ֆունկցիա, uint256
      setApr(ethers.formatUnits(v, 2)); // فرض ենք 2 decimals՝ օրինակ 500 = 5.00%
    } catch (err) {
      console.error("Failed to fetch APR:", err);
      setApr(0);
    }
  };

  useEffect(() => {
    fetchAPR();
    const interval = setInterval(fetchAPR, 30000); // ավտոմատ թարմացում
    return () => clearInterval(interval);
  }, [contractAddress]);

  return (
    <div className="component">
      <h3>{t("economicGrowth")}</h3>
      <p>{apr} %</p>
      <button className="primary" onClick={fetchAPR}>{t("refresh")}</button>
    </div>
  );
}

export default EconomicGrowth;
